local dt = 0
local pt = 0
local player = nil
local velclamp = 20
local W = 240
local H = 136
local GRAV_FORCE = 9.8

local Player = {}
function Player.new(x,y)
	local self = {}
	self.x = x
	self.y = y
	self.velx= 0
	self.vely = 0

	-- could make these local to update
	-- maybe later
	self.rotation = 90 -- really not needed anymore
	self.angle_radians = math.rad(self.rotation)
	
	self.thrust = 30
	function self:bounds()
		-- bounds check
		if self.x>W then
			self.x = 0
		end
		if self.x<0 then
			self.x = W
		end
		if self.y>H then
			self.y = 0
		end
		if self.y<0 then
			self.y = H
		end		
	end
	function self:set_rotation(degrees)
		self.rotation = degrees
		self.angle_radians = math.rad(self.rotation)
	end
	function self:update(dt)
		self.up = false
		self.down = false
		self.left = false
		self.right = false

		-- start of stupid simple
		-- collision handling
		function solid(x,y)
			-- currently 32 is solid
			-- trace(x//8 .. " " .. y//8)
			return mget(x//8,y//8) == 32
		end
		
		-- where are we moving
		local posx = self.x + self.velx*dt
		local posy = self.y + self.vely*dt
		  
		if solid(posx,posy) then
			trace("hit")
			trace(posx//8 .. " " .. posy//8)
		end

		-- move it
		self.x = posx
		self.y = posy

		-- bounds checking
		self:bounds()
		--up
		if btn(0) then
			self.up = true
			self:set_rotation(90)
			local forcex = math.cos(self.angle_radians)*self.thrust*dt
			local forcey = math.sin(self.angle_radians)*self.thrust*dt
			self.velx = self.velx - forcex
			self.vely = self.vely - forcey
		end
		--down
		if btn(1) then
			self.down = true
			self:set_rotation(90)
			local forcex = math.cos(self.angle_radians)*self.thrust*dt
			local forcey = math.sin(self.angle_radians)*self.thrust*dt
			self.velx = self.velx + forcex
			self.vely = self.vely + forcey
		end
		-- left
		if btn(2) then
			self.left = true
			self:set_rotation(180)
			local forcex = math.cos(self.angle_radians)*self.thrust*dt
			local forcey = math.sin(self.angle_radians)*self.thrust*dt
			self.velx = self.velx + forcex
			self.vely = self.vely + forcey
		end
		-- right
		if btn(3) then
			self.right = true
			self:set_rotation(180)
			local forcex = math.cos(self.angle_radians)*self.thrust*dt
			local forcey = math.sin(self.angle_radians)*self.thrust*dt
			self.velx = self.velx - forcex
			self.vely = self.vely - forcey
		end

		-- apply grav no matter what
		self.vely = self.vely + GRAV_FORCE*dt

		-- clamp velocity
		
		if math.abs(self.velx)>velclamp then
			if self.velx>0 then
				self.velx = velclamp
			else
				self.velx = -velclamp
			end
		end
		if math.abs(self.vely)>velclamp then
			if self.vely>0 then
				self.vely = velclamp
			else
				self.vely = -velclamp
			end
		end
		
	end
	function self:draw()
		local sprnum = 1
		local msg = ""
		if self.up then
			sprnum = 2
			msg = "up"
		elseif self.down then
			sprnum = 5
			msg = "down"
		elseif self.left then
			sprnum = 3
			msg = "left"
		elseif self.right then
			sprnum = 4
			msg = "right"
		else
			sprnum = 1
			-- neutral
		end

		-- debug
		pix(self.x,self.y,6)
        pix(self.x+8,self.y+8,6)
        pix(self.x+8,self.y,6)
        pix(self.x,self.y+8,6)

		-- real sprite
		spr(sprnum,self.x,self.y,
		0, -- color key (transparent color)
		1, -- scale
		0, -- flip
		0, -- rotate
		1,1) -- width and height
		print(msg)
	end
	return self
end
function update(dt)
	player:update(dt)
end
function draw()
	cls(0)
	map(0,0,25,15)
	player:draw()
end
function init()
	player = Player.new(100,100)
	-- trace("mget00: " .. mget(0,0))
end
init()
function TIC()
    -- calculate delta time
    dt=time()-pt
	pt=time()
	-- trace(tostring(dt))
	update(dt/1000)
	draw()
end
